mapper.select:
select  from CMS_COMPANY_PROJECT t,CMS_CONTACTS a where a.id=#{contactId} and a.company_id=t.company_id

	and t.COMPANY_TYPE in 

	 	#{item}

select  from CMS_COMPANY_PROJECT t where t.project_id=#{projectId} 

	and t.company_type in 

	 	#{item}

select  from  CMS_PROJECT t where t.id in (select a.project_id from CMS_COMPANY_PROJECT a where a.company_name=#{companyName} 

	and a.company_type in 

	 	#{item}

) 

 and t.CONFIRM_DATE >=${startDate}


 and t.CONFIRM_DATE <${endDate}

select  from CMS_PROJECT t where t.project_name=#{name}

 and t.CONFIRM_DATE >=${startDate}


 and t.CONFIRM_DATE <${endDate}
  limit 0,1


select b.PROJECT_NAME name from CMS_PROJECT b where b.PROJECT_NAME like  CONCAT('%',#{name},'%')

 and b.CONFIRM_DATE >=${startDate}


 and b.CONFIRM_DATE <${endDate}
  LIMIT 0,5

select a.COMPANY_TYPE as role from CMS_COMPANY_PROJECT a where a.PROJECT_ID in
(select t.id from CMS_PROJECT t where t.PROJECT_NAME =#{name})
group by a.COMPANY_TYPE;

select a.PROJECT_POST as role from CMS_CONTACTS_PROJECT a where a.PROJECT_ID in
(select t.id from CMS_PROJECT t where t.PROJECT_NAME =#{name})
group by a.COMPANY_TYPE;
