select * from (select count(t.ID) as nums, substring_index(substring_index(t.category,',',c.help_topic_id+1),',',-1) as industry
from ( SELECT a.* FROM CMS_V_PUBLISH_CONTENT b, CMS_V_PUBLISH_INFO a
) t join mysql.help_topic c on c.help_topic_id < (length(t.category) - length(replace(t.category,',',''))+1) GROUP BY industry order by nums desc) aa
